import React, {useRef,useEffect,useState} from 'react';
import logo from './logo.svg';
import 'react-bulma-components/dist/react-bulma-components.min.css';
import { Navbar,Section,Button } from 'react-bulma-components';
import './App.css';
import { drawDataOnCanvas } from './utils/DrawData';
function App() {
  const canvasRef = useRef(null);
  const [state, setstate] = useState([...Array(100)].map(() => Math.floor(Math.random() * 300 + 40)));
  const [sortingMethodName, setSortingMethodName] = useState('Bubble Sort')
  let ctx = '';
  useEffect(() => {
    ctx = canvasRef.current.getContext('2d');
    drawDataOnCanvas(state,ctx);
  }, [])
  return (
    <div className="App">
      <Navbar color='link'></Navbar>
      <Section className='main'>
        <canvas width='800' height='400' className='canvasElement' ref={canvasRef}></canvas>
      </Section>
      <Button className='button' onClick='alert("asd")' color='link'>Sort using: {sortingMethodName} </Button>
    </div>
  );
}

export default App;
